package challenges

import challenges.Day2BathroomSecuritySuite._
import org.scalatest.{FeatureSpec, Matchers}

class Day2BathroomSecuritySuite extends FeatureSpec with Matchers {

  feature("Part 1") {
    scenario("Basic Scenario 1") {
      val actualInput =
        """
          |ULL
        """.stripMargin

      Day2BathroomSecurity.partOne(actualInput) should be("1")
    }

    scenario("Scenario 1") {
      val actualInput =
        """
          |ULL
          |RRDDD
          |LURDL
          |UUUUD
        """.stripMargin

      Day2BathroomSecurity.partOne(actualInput) should be("1985")
    }

    scenario("Result") {
      println(s"Part 1 result: ${Day2BathroomSecurity.partOne(puzzleInput)}")
    }
  }

  feature("Part 2") {
    scenario("Basic Scenario 1") {
      val actualInput =
        """
          |ULL
        """.stripMargin

      Day2BathroomSecurity.partTwo(actualInput) should be("5")
    }

    scenario("Basic Scenario 2") {
      val actualInput =
        """
          |RRDDD
        """.stripMargin

      Day2BathroomSecurity.partTwo(actualInput) should be("D")
    }

    scenario("Scenario 1") {
      val actualInput =
        """
          |ULL
          |RRDDD
          |LURDL
          |UUUUD
        """.stripMargin

      Day2BathroomSecurity.partTwo(actualInput) should be("5DB3")
    }

    scenario("Result") {
      println(s"Part 2 result: ${Day2BathroomSecurity.partTwo(puzzleInput)}")
    }
  }
}

object Day2BathroomSecuritySuite {

  private val puzzleInput =
    """
      |
      |
      |
      |
      |
    """.stripMargin
}